import NewList from "./NewList";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useState } from "react";

const List = ({ list, onDelete, value }) => {
  // console.log(list);

  const [toggleList, setToggle] = useState(true);
  const [toggleKey, setToggleKey] = useState("");
  const onToggle = async (e) => {
    setToggleKey(e);
    setToggle(!toggleList);
  };

  const deletekey = async (e) => {
    console.log("list", e);
  };
  return (
    <>
      {Object.keys(list).length
        ? Object.keys(list).map((key, i) => (
            <ul type="none" key={key}>
              <li value={key}>
                {Object.keys(list[key]).length ? (
                  toggleList ? (
                    <FaPlusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  ) : (
                    <FaMinusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  )
                ) : null}
                {key}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                  onClick={() => {
                    if (!value) {
                      value = `${value}.${key}`;
                    } else {
                      value = `${key}`;
                    }
                    deletekey(value);
                  }}
                />
              </li>
              {!toggleList ? (
                <NewList list={list[key]} onDelete={onDelete} value={value} />
              ) : null}
            </ul>
          ))
        : null}
    </>
  );
};

export default List;
