import List from "./List";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useState } from "react";

const NewList = ({ list, onListToggle, toggleKey }) => {
  // console.log(list);

  const [toggleList, setToggle] = useState(true);
  const onToggle = (key) => {
    setToggle(!toggleList);
    onListToggle(key);
  };

  return (
    <>
      {Object.keys(list).length
        ? Object.keys(list).map((key, i) => (
            <ul type="none" key={key}>
              <li value={key}>
                {Object.keys(list[key]).length ? (
                  toggleList ? (
                    <FaPlusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={onToggle}
                    />
                  ) : (
                    <FaMinusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={onToggle}
                    />
                  )
                ) : null}
                {key}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                />
              </li>
              {!toggleList ? (
                <List
                  list={list[key]}
                  onListToggle={onListToggle}
                  toggleKey={toggleKey}
                />
              ) : null}
            </ul>
          ))
        : null}
    </>
  );
};

export default NewList;
