import NewList from "./NewList";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useState } from "react";
// import DeletePopup from "./DeletePopup";

const List = ({ list, onDelete, parent }) => {
  const [toggleList, setToggle] = useState({});
  const [toggleKey, setToggleKey] = useState("");
  // const [keyValue, setKeyValue] = useState("");
  // const [toggleDelete, setDeleteToggle] = useState(false);
  // const [parent, setParent] = useState("");

  const onToggle = async (e) => {
    if (toggleList[e] === true || toggleList[e] === false) {
      toggleList[e] = !toggleList[e];
      setToggle(toggleList);
    } else {
      toggleList[e] = true;
      setToggle(toggleList);
      // if (newParent) {
      //   console.log(`${newParent}.${e}`);
      //   setParent(`${newParent}.${e}`);
      // } else {
      //   console.log("else", e);
      //   setParent(e);
      // }
    }

    if (toggleKey === e) {
      setToggleKey(null);
    } else {
      setToggleKey(e);
    }
  };

  const deletekey = async (e) => {
    // console.log("Parent", parent, "\nKey", e);
    // setDeleteToggle(!toggleDelete);
    // let delKey = `${parent}.${e}`;
    // onDelete({ delKey });
  };

  const objLength = Object.keys(list).length;
  if (objLength) {
    return (
      <>
        {Object.keys(list).map((key, i) => (
          <ul type="none" key={key}>
            <li value={key} data-parent={parent ? parent : key}>
              {Object.keys(list[key]).length ? (
                !toggleList[key] ? (
                  <FaPlusSquare
                    style={{
                      fontSize: "0.75rem",
                      paddingRight: "0.25rem",
                      cursor: "pointer",
                    }}
                    onClick={() => onToggle(key)}
                  />
                ) : (
                  <FaMinusSquare
                    style={{
                      fontSize: "0.75rem",
                      paddingRight: "0.25rem",
                      cursor: "pointer",
                    }}
                    onClick={() => onToggle(key)}
                  />
                )
              ) : null}
              {key}{" "}
              <FaTrash
                style={{
                  cursor: "pointer",
                  float: "right",
                  marginRight: "40rem",
                  fontSize: "0.75rem",
                }}
                data-parent={parent ? parent : key}
                onClick={(e) => {
                  // if (Object.keys(list[key]).length) {
                  //   alert(`contains child`);
                  //   return;
                  // } else {
                  //   alert(`sure wanna delete ?`);
                  //   deletekey(key);
                  // }
                  const parent = e.target.dataset.parent;
                  const parent1 = e.target.getAttribute("data-parent");
                  console.log("On Delete call", e, "\n", parent1);

                  deletekey(parent ? parent : key);
                }}
              />
            </li>
            {toggleList[key] ? (
              <NewList
                list={list[key]}
                onDelete={onDelete}
                // value={keyValue}
                parent={parent ? `${parent}.${key}` : key}
              />
            ) : null}
          </ul>
        ))}

        {/* {toggleDelete ? <DeletePopup /> : null} */}
      </>
    );
  }
  return null;
};

export default List;
