import List from "./List";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useState } from "react";

const NewList = ({ list, onDelete, parent }) => {
  const [toggleList, setToggle] = useState({});
  const [toggleKey, setToggleKey] = useState("");

  const onToggle = async (e) => {
    if (toggleList[e] === true || toggleList[e] === false) {
      toggleList[e] = !toggleList[e];
      setToggle(toggleList);
    } else {
      toggleList[e] = true;
      setToggle(toggleList);
    }

    if (toggleKey === e) {
      setToggleKey(null);
    } else {
      setToggleKey(e);
    }
  };

  const deletekey = async (p, e) => {
    console.log("Parent", p, "\nKey", e);
    const delKey = p ? `${p}.${e}` : e;
    console.log("delete Path", delKey);
    onDelete({ delKey });
  };

  return (
    <>
      {list.length
        ? list.map((e) => (
            <ul type="none" key={e.id}>
              <li value={e.id} data-parent={parent}>
                {e.childs.length ? (
                  !toggleList[e.id] ? (
                    <FaPlusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(e.id)}
                    />
                  ) : (
                    <FaMinusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(e.id)}
                    />
                  )
                ) : null}
                {e.name}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                  onClick={(e) => {
                    deletekey(parent, e.id);
                  }}
                />
              </li>
              {toggleList[key] ? (
                <List
                  list={list[key]}
                  onDelete={onDelete}
                  parent={`${parent}.${key}`}
                />
              ) : null}
            </ul>
          ))
        : null}
    </>
  );
};

export default NewList;
