      dataList[obj.parent][obj.text] = {};
    }

    const res = await fetch(`http://localhost:5000/list`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(dataList),
    });
    await res.json();
    // setData(resData);

    const data = await fetchData();
    setData(data);
    const newParents = {};
    for (let key in data) {
      newParents[key] = "";
    }
    setParents(newParents);
  };

