import NewList from "./NewList";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useState } from "react";

const List = ({ list }) => {
  // console.log(list);

  const [toggleList, setToggle] = useState(false);
  const onToggle = (key) => {
    console.log(key);
    if (toggleList === key) {
      setToggle(null);
    }

    setToggle(key);
  };
  return (
    <>
      {Object.keys(list).length
        ? Object.keys(list).map((key, i) => (
            <ul type="none" key={key}>
              <li value={key}>
                {Object.keys(list[key]).length ? (
                  toggleList === key ? (
                    <FaPlusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onDoubleClick={onToggle(key)}
                    />
                  ) : (
                    <FaMinusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onDoubleClick={onToggle(key)}
                    />
                  )
                ) : null}
                {key}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                />
              </li>
              <NewList list={list[key]} />
            </ul>
          ))
        : null}
    </>
  );
};

export default List;
