import NewList from "./NewList";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useState } from "react";

const List = ({ list, onDelete, parent }) => {
  const [toggleList, setToggle] = useState({});
  const [toggleKey, setToggleKey] = useState("");

  const onToggle = async (e) => {
    if (toggleList[e] === true || toggleList[e] === false) {
      toggleList[e] = !toggleList[e];
      setToggle(toggleList);
    } else {
      toggleList[e] = true;
      setToggle(toggleList);
    }

    if (toggleKey === e) {
      setToggleKey(null);
    } else {
      setToggleKey(e);
    }
  };

  const deletekey = async (p, e) => {
    console.log("Parent", p, "\nKey", e);
    // setDeleteToggle(!toggleDelete);
    let delKey = p ? `${p}.${e}` : e;
    console.log("Delete path -->", delKey);
    onDelete({ delKey });
  };

  const listLength = list.length;
  if (listLength) {
    return (
      <>
        {list.map((e) => (
          <ul type="none" key={e.id}>
            <li value={e.id}>
              {e.childs.length ? (
                !toggleList[e.id] ? (
                  <FaPlusSquare
                    style={{
                      fontSize: "0.75rem",
                      paddingRight: "0.25rem",
                      cursor: "pointer",
                    }}
                    onClick={() => onToggle(e.id)}
                  />
                ) : (
                  <FaMinusSquare
                    style={{
                      fontSize: "0.75rem",
                      paddingRight: "0.25rem",
                      cursor: "pointer",
                    }}
                    onClick={() => onToggle(key)}
                  />
                )
              ) : null}
              {key}{" "}
              <FaTrash
                style={{
                  cursor: "pointer",
                  float: "right",
                  marginRight: "40rem",
                  fontSize: "0.75rem",
                }}
                onClick={() => {
                  const delKey = parent ? parent : "";
                  deletekey(delKey, key);
                }}
              />
            </li>
            {toggleList[key] ? (
              <NewList
                list={list[key]}
                onDelete={onDelete}
                parent={parent ? `${parent}.${key}` : key}
              />
            ) : null}
          </ul>
        ))}

        {/* {toggleDelete ? <DeletePopup /> : null} */}
      </>
    );
  }
  return null;
};

export default List;
