import NewList from "./NewList";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useCallback, useEffect, useState } from "react";

const List = ({ list, onDelete, value }) => {
  // console.log(list);

  const [toggleList, setToggle] = useState(false);
  const [toggleKey, setToggleKey] = useState("");
  const [keyValue, setKeyValue] = useState("");
  // const onToggle = async (e) => {

  //   console.log("list", e);
  //   setToggleKey(e);
  //   if (toggleKey === e) {
  //     setToggle(!toggleList);
  //   }
  // };

  const onToggle = useCallback((e) => {
    setToggleKey(e);
    if (toggleKey === e) {
      setToggle(!toggleList);
    }
  }, []);

  const deletekey = async (e) => {
    // console.log("list", e);
  };

  useEffect(() => {
    let newKey;
    // console.log(value);
    if (value && keyValue) {
      newKey = `${keyValue}.${value}`;
    } else if (value) {
      newKey = value;
    }
    // console.log(newKey);
    // console.log(keyValue);
    setKeyValue(newKey);
  }, [value]);

  return (
    <>
      {Object.keys(list).length
        ? Object.keys(list).map((key, i) => (
            <ul type="none" key={key}>
              <li value={key}>
                {Object.keys(list[key]).length ? (
                  !toggleList && toggleKey !== key ? (
                    <FaPlusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  ) : (
                    <FaMinusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  )
                ) : null}
                {key}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                  onClick={() => {
                    let newKey;
                    if (!value && !keyValue) {
                      newKey = `${value}.${key}`;
                      setKeyValue(newKey);
                    } else {
                      setKeyValue(key);
                    }
                    deletekey(keyValue);
                  }}
                />
              </li>
              {toggleList && toggleKey === key ? (
                <NewList
                  list={list[key]}
                  onDelete={onDelete}
                  value={keyValue}
                />
              ) : null}
            </ul>
          ))
        : null}
    </>
  );
};

export default List;
