import NewList from "./NewList";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useState } from "react";

const List = ({ list }) => {
  // console.log(list);

  const [toggleList, setToggle] = useState(true);
  const onToggle = (key) => {
    setToggle(!toggleList);
  };
  return (
    <>
      {Object.keys(list).length
        ? Object.keys(list).map((key, i) => (
            <ul type="none" key={key}>
              <li value={key}>
                {Object.keys(list[key]).length ? (
                  toggleList ? (
                    <span onClick={onToggle} value={key}>
                      <FaPlusSquare
                        style={{
                          fontSize: "0.75rem",
                          paddingRight: "0.25rem",
                          cursor: "pointer",
                        }}
                      />
                    </span>
                  ) : (
                    <span onClick={onToggle} value={key}>
                      <FaMinusSquare
                        style={{
                          fontSize: "0.75rem",
                          paddingRight: "0.25rem",
                          cursor: "pointer",
                        }}
                      />
                    </span>
                  )
                ) : null}
                {key}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                />
              </li>
              {!toggleList ? <NewList list={list[key]} /> : null}
            </ul>
          ))
        : null}
    </>
  );
};

export default List;
