import List from "./List";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useState } from "react";

const NewList = ({ list, onDelete, parent }) => {
  const [toggleList, setToggle] = useState({});
  const [toggleKey, setToggleKey] = useState("");
  // const [keyValue, setKeyValue] = useState("");
  const [newParent, setParent] = useState("");

  const onToggle = async (e) => {
    if (toggleList[e] === true || toggleList[e] === false) {
      toggleList[e] = !toggleList[e];
      setToggle(toggleList);
    } else {
      toggleList[e] = true;
      setToggle(toggleList);
      console.log(`${parent}.${e}`);
      setParent(`${parent}.${e}`);
    }

    if (toggleKey === e) {
      setToggleKey(null);
    } else {
      setToggleKey(e);
    }
  };

  const deletekey = async (p, e) => {
    console.log("Parent", p, "\nKey", e);
    const delKey = p ? `${p}.${e}` : e;
    console.log("delete Path", delKey);
    onDelete({ delKey });
  };

  return (
    <>
      {Object.keys(list).length
        ? Object.keys(list).map((key, i) => (
            <ul type="none" key={key}>
              <li value={key} data-parent={parent}>
                {Object.keys(list[key]).length ? (
                  !toggleList[key] ? (
                    <FaPlusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  ) : (
                    <FaMinusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  )
                ) : null}
                {key}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                  onClick={(e) => {
                    // if (Object.keys(list[key]).length) {
                    //   alert(`coontains child`);
                    //   return;
                    // }
                    // console.log(newParent);
                    deletekey(parent, key);
                  }}
                />
              </li>
              {toggleList[key] ? (
                <List
                  list={list[key]}
                  onDelete={onDelete}
                  parent={`${parent}.${key}`}
                />
              ) : null}
            </ul>
          ))
        : null}
    </>
  );
};

export default NewList;
