import List from "./List";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useEffect, useState } from "react";

const NewList = ({ list, onDelete, value }) => {
  // console.log(list);

  const [toggleList, setToggle] = useState({});
  const [toggleKey, setToggleKey] = useState("");
  const [keyValue, setKeyValue] = useState("");

  const onToggle = async (e) => {
    if (toggleList[e] === true || toggleList[e] === false) {
      toggleList[e] = !toggleList[e];
      setToggle(toggleList);
    } else {
      toggleList[e] = true;
      setToggle(toggleList);
    }

    if (toggleKey === e) {
      setToggleKey(null);
    } else {
      setToggleKey(e);
    }
  };
  const deletekey = async () => {
    console.log("newList", e);
    // console.log(e.target.value);
  };

  useEffect(() => {
    let newKey = "";
    // console.log(value, "\n", newKey);
    if (value && keyValue) {
      newKey = `${keyValue}.${value}`;
    } else if (value) {
      newKey = value;
    }
    // console.log(newKey);
    // console.log(keyValue);
    setKeyValue(newKey);
  }, [value]);

  return (
    <>
      {Object.keys(list).length
        ? Object.keys(list).map((key, i) => (
            <ul type="none" key={key}>
              <li value={key}>
                {Object.keys(list[key]).length ? (
                  !toggleList[key] ? (
                    <FaPlusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  ) : (
                    <FaMinusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  )
                ) : null}
                {key}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                  onClick={() => {
                    deletekey(keyValue);
                  }}
                />
              </li>
              {toggleList[key] ? (
                <List list={list[key]} onDelete={onDelete} value={keyValue} />
              ) : null}
            </ul>
          ))
        : null}
    </>
  );
};

export default NewList;
