import NewList from "./NewList";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";

const List = ({ list, onToggle, toggleList }) => {
  console.log("toggleList", toggleList);

  let iconStyle = {
    fontSize: "0.75rem",
    paddingRight: "0.25rem",
    cursor: "pointer",
  };
  return (
    <ul type="none">
      {Object.keys(list).length
        ? Object.keys(list).map((k) => (
            <>
              <li key={k}>
                {Object.keys(list[k]).length ? (
                  toggleList === k ? (
                    <span onClick={onToggle(k)}>
                      <FaMinusSquare style={iconStyle} />{" "}
                    </span>
                  ) : (
                    <span onClick={onToggle(k)}>
                      <FaPlusSquare style={iconStyle} />{" "}
                    </span>
                  )
                ) : null}
                {k}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                />
              </li>
              <NewList
                list={list[k]}
                onToggle={onToggle}
                toggleList={toggleList}
              />
            </>
          ))
        : ""}
    </ul>
  );
};

export default List;
