import List from "./List";
import { FaMinusSquare, FaPlusSquare, FaTrash } from "react-icons/fa";
import { useState } from "react";

const NewList = ({ list, onDelete }) => {
  // console.log(list);

  const [toggleList, setToggle] = useState(true);
  const [toggleKey, setToggleKey] = useState("newlist");
  const onToggle = async (e) => {
    setToggleKey(e);
    setToggle(!toggleList);
  };
  const deletekey = async (e) => {
    console.log("newList", e);
    // console.log(e.target.value);
  };

  return (
    <>
      {Object.keys(list).length
        ? Object.keys(list).map((key, i) => (
            <ul type="none" key={key}>
              <li value={key}>
                {Object.keys(list[key]).length ? (
                  toggleList &&
                  (toggleKey === key || toggleKey === "newlist") ? (
                    <FaPlusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  ) : (
                    <FaMinusSquare
                      style={{
                        fontSize: "0.75rem",
                        paddingRight: "0.25rem",
                        cursor: "pointer",
                      }}
                      onClick={() => onToggle(key)}
                    />
                  )
                ) : null}
                {key}{" "}
                <FaTrash
                  style={{
                    cursor: "pointer",
                    float: "right",
                    marginRight: "40rem",
                    fontSize: "0.75rem",
                  }}
                  onClick={() => deletekey(key)}
                />
              </li>
              {!toggleList && toggleKey === key ? (
                <List list={list[key]} onDelete={onDelete} />
              ) : null}
            </ul>
          ))
        : null}
    </>
  );
};

export default NewList;
