import React from "react";

const ListIcon = ({ toggleList }) => {
  const onToggle = async (e) => {
    if (toggleList[e] === true || toggleList[e] === false) {
      toggleList[e] = !toggleList[e];
      setToggle(toggleList);
    } else {
      toggleList[e] = true;
      setToggle(toggleList);
    }

    if (toggleKey === e) {
      setToggleKey(null);
    } else {
      setToggleKey(e);
    }
  };

  return (
    <>
      ( toggleList ? (
      <FaPlusSquare
        style={{
          fontSize: "0.75rem",
          paddingRight: "0.25rem",
          cursor: "pointer",
        }}
        onClick={() => onToggle(e.id)}
      />
      ) : (
      <FaMinusSquare
        style={{
          fontSize: "0.75rem",
          paddingRight: "0.25rem",
          cursor: "pointer",
        }}
        onClick={() => onToggle(e.id)}
      />
      ) )
    </>
  );
};

export default ListIcon;
