import { useEffect, useState } from "react";

const AddItem = ({ parents }) => {
  const [text, setText] = useState("");
  const [heirarchy, setHeirarchy] = useState("");
  const [parent, setParent] = useState("");

  useEffect(() => {
    setParent(parents);
  }, [parents, parent]);

  const changeHeirarchy = (e) => {
    e.preventDefault();
    const data = e.target.value;
    setHeirarchy(data);
  };

  const onSubmit = (e) => {
    e.preventDefault();

    if (!text) {
      alert(`Please enter item text`);
      return;
    }

    if (!heirarchy) {
      alert(`Please select heirarchy`);
      return;
    }

    if (!heirarchy) {
      alert(`Please select heirarchy`);
      return;
    }

    if (!parent) {
      alert(`Please select heirarchy`);
      return;
    }
  };
  return (
    <div>
      <form className="add-form">
        <div className="form-control">
          <label>Enter Item</label>
          <input
            type="text"
            placeholder="Enter item"
            value={text}
            onChange={(e) => setText(e.target.value)}
          ></input>
        </div>
        <div className="form-control">
          <label>Hierarchy</label>
          <select onChange={changeHeirarchy} value={heirarchy}>
            <option value="" defaultValue>
              Select
            </option>
            <option value="parent">Parent</option>
            {Object.keys(parent).length && <option value="child">Child</option>}
          </select>
        </div>
        {Object.keys(parent).length
          ? heirarchy === "child" && (
              <div className="form-control">
                <label>Parent</label>
                <select>
                  <option value="" defaultValue>
                    Select
                  </option>
                  {Object.keys(parent).map((key) => (
                    <option key={key} value={key}>
                      {key}
                    </option>
                  ))}
                </select>
              </div>
            )
          : ""}
        <input type="submit" value="Submit" className="btn btn-clock" />
      </form>
    </div>
  );
};

export default AddItem;
